package main

import (
	"fmt"
	"math/rand"
	"time"
)

// True random numbers
// pseudo random numbers(generated by algorithms)
func main() {
	fmt.Println(rand.Intn(101))
	//if we want to generate numbers between 1 and (inclusive)
	fmt.Println(rand.Intn(100) + 1)

	val := rand.New(rand.NewSource(40))
	//fmt.Println(val.Intn(101))

	fmt.Println(val.Intn(6) + 5)

	val1 := rand.New(rand.NewSource(time.Now().Unix()))
	fmt.Println(val1.Intn(101))

	fmt.Println("=========Floating Point==========")
	fmt.Println(rand.Float64()) //between 0.0 and 1.0, 1.0 will not be included in the interval

	for {
		//Show the menu
		fmt.Println("Welcome to the Dice Game")
		fmt.Println("1. Roll the dice")
		fmt.Println("2. Exit")
		fmt.Println("Enter your choice (1 or 2): ")

		var choice int
		_, err := fmt.Scan(&choice)

		if err != nil || (choice != 1 && choice != 2) {
			fmt.Println("Invalid choice, please enter 1 or 2")
			continue
		}
		if choice == 2 {
			fmt.Println("Thanks for playing! Goodbye")
			break
		}

		die1 := rand.Intn(6) + 1
		die2 := rand.Intn(6) + 1

		//show the results
		fmt.Printf("You rolled a %d and a %d\n", die1, die2)
		fmt.Println("Total:", die1+die2)

		//Ask if the user wants to roll the die again
		fmt.Print("Do you want to roll again? (y/n):")
		var rollAgain string
		_, err = fmt.Scan(&rollAgain)
		if err != nil || (rollAgain != "y" && rollAgain != "n") {
			fmt.Println("Invalid Input, assuming no.")
			return
		}
		if rollAgain == "n" {
			fmt.Println("Thanks for playing")
		}
	}
}
